import React, { useState, useEffect } from 'react';
import 'ol/ol.css';
import { XYZ } from 'ol/source';
import { Map as AllMap, View } from 'ol';
import { fromLonLat, get as getProjection } from 'ol/proj';
import { Tile as TileLayer } from 'ol/layer';
import { OSM } from 'ol/source';
import Modal from 'react-modal'; // Import Modal from react-modal
import { useRecoilState } from 'recoil'; // Import useRecoilState from Recoil
import { popUpModal } from './yourRecoilAtoms'; // Adjust the import path based on your Recoil atom

const customModalStyles = {
    overlay: {
        backgroundColor: "rgba(0, 0, 0, 0.4)",
        width: "100%",
        height: "100vh",
        zIndex: "10",
        position: "fixed",
        top: "0",
        left: "0",
    },
    content: {
        width: "360px",
        height: "180px",
        zIndex: "150",
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        borderRadius: "10px",
        boxShadow: "2px 2px 2px rgba(0, 0, 0, 0.25)",
        backgroundColor: "white",
        justifyContent: "center",
        overflow: "auto",
    },
};

const Map = () => {
    const [mapObject, setMapObject] = useState({});
    const [modalOpen, setModalOpen] = useRecoilState(popUpModal);

    useEffect(() => {
        const map = new AllMap({
            layers: [
                new TileLayer({
                    source: new XYZ({ url: "http://xdworld.vworld.kr:8080/2d/Base/202002/{z}/{x}/{y}.png" }),
                })
            ],
            target: 'map',
            view: new View({
                projection: getProjection('EPSG:3857'),
                center: fromLonLat([126.752, 37.4713], 'EPSG:3857'),
                zoom: 13
            })
        });

        setMapObject({ map });

        // Clean up function
        return () => {
            if (map) {
                map.setTarget(null);
            }
        };
    }, []);

    const handleButtonClick = () => {
        // Open the modal when the button is clicked
        setModalOpen(true);
    };

    return (
        <div>
            <div id="map" style={{ height: '100rem' }}></div>
            <button onClick={handleButtonClick}>Change Map Center</button>

            {/* PopupMessage component */}
            <Modal
                isOpen={modalOpen}
                onRequestClose={() => setModalOpen(false)}
                style={customModalStyles}
                ariaHideApp={false}
                contentLabel="Pop up Message"
                shouldCloseOnOverlayClick={false}
            >
                {/* Components to be included in the modal window */}
                {/* You can add your message content or any other components here */}
            </Modal>
        </div>
    );
}

export default Map;
